package com.syntax.class2;

public class Loop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=1;
		
		while (a<=20) {
			System.out.print(a);
			
			a++;
			
			
		}
		
		int x=50;
		
		while (x>=1) {
			
			System.out.print(x+" ");
			x--;
			
		}
	}

}
