package com.syntax.class2;

public class loop2EvenNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// we want to print even numbers
		
		
		int n=2;
		
		while(n<=10) {
			
			System.out.println(n+" ");
			
			n+=2;	
		}
		// Another way of Even number adding 
		
		int e=2;
		
		while (e<=39) {
			
		if(e%2==0) {
			System.out.print(e+" ");
		}
		e++;
		
		}
	}

}
